#!/usr/bin/env node

"use strict"





const doc = `
Name:
	password123

Usage:
	password123 <password>...   [--plain | --hash]
	password123 (-s | --secure) [--plain | --hash]
	password123 (-h | --help | --version)

Version:
	0.1.0

Description:

Arguments:
	<password>...    The password(s) to test.

Options:
	--s, --secure    .
	--version        .
`





const docopt      = require('docopt').docopt
const password123 = require('password123/app/password123')
const args        = docopt(doc)





password123(args)
